/* header */
.head-address{
    font-size: 10px;
    width: 270px;
}

hr {
    border-top: 1px solid white !important
}

#header-html .print-heading{
    border-bottom: 1px solid white;
}

/* table */
.print-format thead tr th{
    background-color: #1b3c6c !important;
    color: white;
    text-align: center;
}
.print-format tfoot tr th{
    background-color: white !important;
    color: black;
}
th.table-sr{
 text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
}
th.table-sr:after{
   content: "No";
  text-indent: 0;
  display: block;
  line-height: initial; /* New content takes up original line height */
}

table {
    border:none;
}
table tr td, table tr th {
    border: 1.7px solid #6e6e6e !important;
}

.total-row {
    border: none !important;
}
.total-row th {
    text-align: right !important;
    border: none !important;
}

.total-row td {
    font-weight: bold;
    border: 1.7px solid #6e6e6e;
}
td .value small{
    font-size: .8em;
}

/* footer */
.footer-notes {
    padding-bottom: 3em;
    padding-top: 0.7em;
}

.footerinfo {
    background: #6D6D6D;
    color: white;
    font-size: 10px;
}
