css:
/* header */
.head-address{
    font-size: 10px;
    width: 270px;
}

hr {
    border-top: 1px solid white !important
}

#header-html .print-heading{
    border-bottom: 1px solid white;
}

/* table */
.print-format th{
    background-color: #ffffff !important;
    color: black;
}
th.table-sr{
 text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
}
th.table-sr:after{
   content: "No";
  text-indent: 0;
  display: block;
  line-height: initial; /* New content takes up original line height */
}

table {
    border:none;
}
table tr td, table tr th {
    border: 1.7px solid #6e6e6e !important;
}

.total-row {
    border: none !important;
}
.total-row th {
    text-align: right !important;
    border: none !important;
}

.total-row td {
    font-weight: bold;
    border: 1.7px solid #6e6e6e;
}
td .value small{
    font-size: .8em;
}

/* footer */
.footer-notes {
    padding-bottom: 3em;
    padding-top: 0.7em;
}

.footerinfo {
    background: #6D6D6D;
    color: white;
    font-size: 10px;
}

.tbl-items th {
    background-color: #1a3c6c !important;
    color: white;
}
@media print {
 #footer-html {
    display: block;
    position: fixed;
    bottom: 0;
    left: 0;
    width: 100%;
 }
}
